#!/bin/bash

# Azure Hub-Spoke Service Principal Setup Script
# Creates dedicated service principals for hub and spoke environments
# Requires Owner permissions on both subscriptions

set -euo pipefail

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Logging function
log() {
    echo -e "${BLUE}[$(date +'%Y-%m-%d %H:%M:%S')]${NC} $1"
}

error() {
    echo -e "${RED}[ERROR]${NC} $1" >&2
}

success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

warn() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

# Check if required parameters are provided
if [ $# -lt 2 ]; then
    error "Usage: $0 <hub_subscription_id> <spoke_subscription_id> [environment_name]"
    error "Example: $0 12345678-1234-1234-1234-123456789012 87654321-4321-4321-4321-210987654321 dev"
    exit 1
fi

HUB_SUBSCRIPTION_ID="$1"
SPOKE_SUBSCRIPTION_ID="$2"
ENVIRONMENT_NAME="${3:-prod}"

# Service principal names
HUB_SP_NAME="sp-hub-terraform-${ENVIRONMENT_NAME}"
SPOKE_SP_NAME="sp-spoke-terraform-${ENVIRONMENT_NAME}"

# Output file for credentials
CREDENTIALS_FILE="./service-principal-credentials-${ENVIRONMENT_NAME}.json"

log "Starting service principal setup for environment: ${ENVIRONMENT_NAME}"
log "Hub Subscription ID: ${HUB_SUBSCRIPTION_ID}"
log "Spoke Subscription ID: ${SPOKE_SUBSCRIPTION_ID}"

# Check if user is logged in to Azure CLI
if ! az account show >/dev/null 2>&1; then
    error "Please login to Azure CLI first: az login"
    exit 1
fi

# Function to check if user has required permissions
check_permissions() {
    local subscription_id="$1"
    local subscription_name="$2"
    
    log "Checking permissions for ${subscription_name} subscription (${subscription_id})"
    
    # Set active subscription
    az account set --subscription "${subscription_id}" || {
        error "Failed to set subscription ${subscription_id}. Please check subscription ID and access."
        exit 1
    }
    
    # Check if user has Owner or User Access Administrator role
    local role_assignments
    role_assignments=$(az role assignment list --assignee "$(az account show --query user.name -o tsv)" --subscription "${subscription_id}" --query "[?roleDefinitionName=='Owner' || roleDefinitionName=='User Access Administrator'].roleDefinitionName" -o tsv)
    
    if [ -z "$role_assignments" ]; then
        error "Insufficient permissions for ${subscription_name} subscription. Owner or User Access Administrator role required."
        exit 1
    fi
    
    success "Permissions verified for ${subscription_name} subscription"
}

# Function to create service principal
create_service_principal() {
    local sp_name="$1"
    local subscription_id="$2"
    local role_scope="$3"
    
    log "Creating service principal: ${sp_name}"
    
    # Check if service principal already exists
    local existing_sp
    existing_sp=$(az ad sp list --display-name "${sp_name}" --query "[0].appId" -o tsv 2>/dev/null || echo "")
    
    if [ -n "$existing_sp" ] && [ "$existing_sp" != "null" ]; then
        warn "Service principal '${sp_name}' already exists with App ID: ${existing_sp}"
        echo "$existing_sp"
        return 0
    fi
    
    # Create service principal with Contributor role
    local sp_info
    sp_info=$(az ad sp create-for-rbac \
        --name "${sp_name}" \
        --role "Contributor" \
        --scopes "${role_scope}" \
        --json-auth 2>/dev/null)
    
    if [ $? -eq 0 ]; then
        local app_id
        app_id=$(echo "$sp_info" | jq -r '.clientId')
        success "Created service principal: ${sp_name} (App ID: ${app_id})"
        echo "$app_id"
    else
        error "Failed to create service principal: ${sp_name}"
        exit 1
    fi
}

# Function to assign additional roles
assign_role() {
    local app_id="$1"
    local role="$2"
    local scope="$3"
    local description="$4"
    
    log "Assigning ${role} role to ${app_id} for ${description}"
    
    # Check if role assignment already exists
    local existing_assignment
    existing_assignment=$(az role assignment list --assignee "${app_id}" --role "${role}" --scope "${scope}" --query "[0].id" -o tsv 2>/dev/null || echo "")
    
    if [ -n "$existing_assignment" ] && [ "$existing_assignment" != "null" ]; then
        warn "Role assignment already exists: ${role} for ${description}"
        return 0
    fi
    
    # Assign role
    if az role assignment create --assignee "${app_id}" --role "${role}" --scope "${scope}" >/dev/null 2>&1; then
        success "Assigned ${role} role for ${description}"
    else
        error "Failed to assign ${role} role for ${description}"
        exit 1
    fi
}

# Check permissions for both subscriptions
check_permissions "$HUB_SUBSCRIPTION_ID" "hub"
check_permissions "$SPOKE_SUBSCRIPTION_ID" "spoke"

# Create hub service principal
log "Creating hub service principal..."
az account set --subscription "$HUB_SUBSCRIPTION_ID"
HUB_SP_APP_ID=$(create_service_principal "$HUB_SP_NAME" "$HUB_SUBSCRIPTION_ID" "/subscriptions/${HUB_SUBSCRIPTION_ID}")

# Create spoke service principal
log "Creating spoke service principal..."
az account set --subscription "$SPOKE_SUBSCRIPTION_ID"
SPOKE_SP_APP_ID=$(create_service_principal "$SPOKE_SP_NAME" "$SPOKE_SUBSCRIPTION_ID" "/subscriptions/${SPOKE_SUBSCRIPTION_ID}")

# Wait for service principals to propagate
log "Waiting for service principals to propagate..."
sleep 30

# Assign cross-subscription permissions
log "Configuring cross-subscription permissions..."

# Hub SP needs Network Contributor on spoke for VNet peering
assign_role "$HUB_SP_APP_ID" "Network Contributor" "/subscriptions/${SPOKE_SUBSCRIPTION_ID}" "spoke subscription (for VNet peering)"

# Hub SP needs DNS Zone Contributor on spoke for policy-based DNS record management
assign_role "$HUB_SP_APP_ID" "Private DNS Zone Contributor" "/subscriptions/${SPOKE_SUBSCRIPTION_ID}" "spoke subscription (for DNS automation)"

# Spoke SP needs Network Contributor on hub for VNet peering
assign_role "$SPOKE_SP_APP_ID" "Network Contributor" "/subscriptions/${HUB_SUBSCRIPTION_ID}" "hub subscription (for VNet peering)"

# Store credentials for easy access
log "Saving service principal credentials..."

# Get service principal details
az account set --subscription "$HUB_SUBSCRIPTION_ID"
HUB_SP_SECRET=$(az ad sp credential reset --id "$HUB_SP_APP_ID" --query password -o tsv)
HUB_TENANT_ID=$(az account show --query tenantId -o tsv)

az account set --subscription "$SPOKE_SUBSCRIPTION_ID"
SPOKE_SP_SECRET=$(az ad sp credential reset --id "$SPOKE_SP_APP_ID" --query password -o tsv)
SPOKE_TENANT_ID=$(az account show --query tenantId -o tsv)

# Create credentials file
cat > "$CREDENTIALS_FILE" << EOF
{
  "environment": "${ENVIRONMENT_NAME}",
  "created_at": "$(date -u +"%Y-%m-%dT%H:%M:%SZ")",
  "hub": {
    "subscription_id": "${HUB_SUBSCRIPTION_ID}",
    "tenant_id": "${HUB_TENANT_ID}",
    "client_id": "${HUB_SP_APP_ID}",
    "client_secret": "${HUB_SP_SECRET}",
    "service_principal_name": "${HUB_SP_NAME}"
  },
  "spoke": {
    "subscription_id": "${SPOKE_SUBSCRIPTION_ID}",
    "tenant_id": "${SPOKE_TENANT_ID}",
    "client_id": "${SPOKE_SP_APP_ID}",
    "client_secret": "${SPOKE_SP_SECRET}",
    "service_principal_name": "${SPOKE_SP_NAME}"
  }
}
EOF

chmod 600 "$CREDENTIALS_FILE"

success "Service principal setup completed!"
success "Credentials saved to: ${CREDENTIALS_FILE}"

